var memory = {
		totalOpen: 0,
		totalSolved: 0,
		elements: {
				tiles: undefined,
				board: undefined,
				overlay: undefined
		},
		tilePaths: icons,
		lastTitleClicked: undefined,
		init: function() {

				this.createGameBoard();
				this.buildGameTiles();

				// Init game events
				this.events();

		},
		events: function() {

				memory.elements.tiles = document.getElementsByClassName("tile");

				for (var i = 0; i < memory.elements.tiles.length; i++) {
						memory.elements.tiles[i].onclick = this.determineEvent;
				}

		},
		createGameBoard: function() {

				// Create game board
				memory.elements.board = document.createElement('div');

				// Add tile attributes
				memory.elements.board.setAttribute('id', 'gameBoard');

				// Add game board
				document.body.appendChild(memory.elements.board);

				// Build game overlay
				memory.elements.overlay = document.createElement('div');
				memory.elements.overlay.setAttribute('class', 'overlay');
				memory.elements.overlay.innerHTML = '<p>Well done, you finished the game.</p>';

				// Add to game board
				memory.elements.board.appendChild(memory.elements.overlay);

		},
		buildGameTiles: function() {

				// Duplicate icons so they have a match
				memory.tilePaths = memory.tilePaths.concat(memory.tilePaths);

				// Shuffle array for random order
				memory.tilePaths = memory.tilePaths.sort(function() {
						return 0.5 - Math.random()
				});

				for (var i = 0; i < memory.tilePaths.length; i++) {

						var tiles = memory.tilePaths[i];

						// Create tile
						var tileEl = document.createElement('button');

						tileEl.innerHTML = '<svg class="tile__svg" width="100%" height="100%" viewBox="0 0 160 160" enable-background="new 0 0 160 160" preserveAspectRatio="none"><path d="' + memory.tilePaths[i][0] + '" /></svg>';

						// Add tile attributes & content
						tileEl.setAttribute('class', 'tile');
						tileEl.setAttribute('data-icon', memory.tilePaths[i][1]);
						tileEl.setAttribute('data-default', memory.tilePaths[i][0]);
						tileEl.setAttribute('data-icon-visible', 'false');
						tileEl.setAttribute('data-solved', 'false');

						// Add tite to game board
						memory.elements.board.appendChild(tileEl);

				}

		},
		determineEvent: function() {

				var self = this;

				memory.totalOpen++;

				// Dont run if two already open
				if (memory.totalOpen <= 2) {

						// Open tile and see if match
						memory.animateSvg(self);

						if (memory.lastTileClicked) {

								// Check if match
								if (memory.lastTileClicked.getAttribute('data-icon') === self.getAttribute('data-icon')) {

										// Add solved true so tiles dont reanimate
										self.setAttribute('data-solved', 'true');
										self.classList.add('tile--solved');
										memory.lastTileClicked.setAttribute('data-solved', 'true');
										memory.lastTileClicked.classList.add('tile--solved');

										// Increment count for total solved
										memory.totalSolved++;

										// Check to see if more moves are possible or game finished
										memory.isGameFinished();

								} else {

										if (memory.totalOpen === 2) {

												// Wait for icon to animate before resetting
												setTimeout(function() {
														// Loop over tiles
														for (var i = 0; i < memory.elements.tiles.length; i++) {

																// If tile has visible icon, run animate function so they return to squares
																if (memory.elements.tiles[i].getAttribute('data-icon-visible') === 'true') {
																		memory.animateSvg(memory.elements.tiles[i]);
																}

														}

												}, 1000);

										}
								}

						}

				}

				if (memory.totalOpen === 2) {
						setTimeout(function() {
								// Reset, since move finished
								memory.totalOpen = 0;
								memory.lastTileClicked = undefined;
						}, 1000);

				} else {
						// Store tile, so we can check it against next clicked tile
						memory.lastTileClicked = self;
				}

		},
		animateSvg: function(self) {

				var pathIcon = self.getAttribute('data-icon'),
						pathDefault = self.getAttribute('data-default'),
						s = Snap(self.querySelector('.tile__svg')),
						path = s.select('path'),
						iconVisible = self.getAttribute('data-icon-visible');

				if (self.getAttribute('data-solved') === 'false') {

						path.stop().animate({
								'path': (iconVisible === 'true') ? pathDefault : pathIcon
						}, 300, mina.easeout);

						if (iconVisible === 'true') {
								iconVisible = 'false';
								self.disabled = false;
						} else {
								iconVisible = 'true';
								self.disabled = true;
						}

						self.setAttribute('data-icon-visible', iconVisible);

				}

		},
		isGameFinished: function() {

				// Check if total solved is equal to the amount of unique tile paths
				if (memory.totalSolved === (memory.tilePaths.length / 2)) {
					memory.elements.overlay.classList.add('overlay--active')
				}
		}

};
memory.init();
